INSERT INTO all_data(Course_offering_ID,New,Course_Name,Credits,Time_and_Place,semester,Professor_Name,First_Year_Friendly,Max_students,Course_Description) VALUES ('PHYS101','.','Physics II','4','MWF 2:10-3:05 PM in FSH-102','S18','Michael Bergman','WP',40,'This course continues the calculus-based physics sequence begun in Physics 100. Topics include thermodynamics, electricity, magnetism, optics, special relativity, and wave mechanics. Accompanying laboratory required. Prerequisite: Physics 100. Corequisite: Mathematics 211. (This course has a lab fee.)');
